import { useDispatch, useSelector } from "react-redux";
import {toast} from "react-hot-toast";
import { Toaster } from "react-hot-toast";
import {add , remove} from "../redux/Slices/cartSlice"
const Product = ({posts}) => {
  // const posts = props.posts;
    const {cart}=useSelector((state)=>state);
    const dispatch = useDispatch();
 
    const Removefromcarthanndler=()=>{
      dispatch(remove(posts.id));
      toast.error("Item removed from cart");
      <Toaster/>
             }

    const addtocarthandler=()=> {

      dispatch(add(posts));
      toast.success("Item added to cart");
      <Toaster/>

    }

  if (!posts) {
    return null; // or handle the scenario when posts is undefined
  }
  return <div>
 

   { <div><p className="font-bold">{posts.title}</p></div>}
    <div > <p className="font-semibold">{posts.description.split(" ").slice(0,10)}</p> </div>
    <div>
      <img src={posts.image} alt="" /></div>
    <div>${posts.price}</div>
    {/* <div>{posts.title}</div> */}


    {
      cart.some((p)=>p.id === posts.id)?(<button onClick={Removefromcarthanndler}>Remove Item</button>): (<button onClick={addtocarthandler}>Add Item</button>)
    }

  </div>;
};

export default Product;
