import { useSelector } from 'react-redux'

const Counter = () => {

const count = useSelector((state)=> state.counter.value)
const inc = useSelector((state)=> state.reducer.value)

  return (
    <div>Counter

        <div>
                    {
                        count
                    }
        </div>
        <button  className='button bg-black font-white text-white'>Incr</button>
        <br />
        <br />
        <button>Decr</button>
    </div>
  )
}

export default Counter
